void Screen::UpdateStandardDouble(int minimumX, int y, double oldDouble, double newDouble, int decimalPlaces) {
  if (oldDouble == newDouble) { return; }
  PrintStandardDouble(minimumX, y, oldDouble, decimalPlaces, ILI9341_WHITE);
  PrintStandardDouble(minimumX, y, newDouble, decimalPlaces, ILI9341_BLACK);
}

void Screen::PrintStandardDouble(int minimumX, int y, double number, int decimalPlaces, int color) {
  if (number < 10) {
    Print(minimumX + 40, y, number, decimalPlaces, color);
  } else if (number < 100) {
    Print(minimumX + 20, y, number, decimalPlaces, color);
  } else {
    Print(minimumX, y, number, decimalPlaces, color);
  }
}

void Screen::UpdateOutput(double newOutput) {
  if (newOutput == 0) {
    FillOutputBars(false, false, false, false, false);
  }
  else if (newOutput < 20) {
    FillOutputBars(true, false, false, false, false);
  }
  else if (newOutput < 40) {
    FillOutputBars(true, true, false, false, false);
    }
  else if (newOutput < 60) {
    FillOutputBars(true, true, true, false, false);
    }
  else if (newOutput < 80) {
    FillOutputBars(true, true, true, true, false);
    }
  else if (newOutput <= 100) {
    FillOutputBars(true, true, true, true, true);
    }
}

void Screen::FillOutputBars(bool barOne, bool barTwo, bool barThree, bool barFour, bool barFive) {
  if(barOne) { _tft.fillRect(185, 145, 13, 6, ILI9341_BLACK); }
  if(!barOne) { _tft.fillRect(185, 145, 13, 6, ILI9341_WHITE); }
  if(barTwo) { _tft.fillRect(212, 137, 13, 14, ILI9341_BLACK); }
  if(!barTwo) { _tft.fillRect(212, 137, 13, 14, ILI9341_WHITE); }
  if(barThree) { _tft.fillRect(239, 129, 13, 22, ILI9341_BLACK); }
  if(!barThree) { _tft.fillRect(239, 129, 13, 22, ILI9341_WHITE); }
  if(barFour) { _tft.fillRect(266, 121, 13, 30, ILI9341_BLACK); }
  if(!barFour) { _tft.fillRect(266, 121, 13, 30, ILI9341_WHITE); }
  if(barFive) { _tft.fillRect(293, 113, 13, 38, ILI9341_BLACK); }
  if(!barFive) { _tft.fillRect(293, 113, 13, 38, ILI9341_WHITE); }
}

