unsigned long Screen::testLines(uint16_t color) {
  unsigned long start, t;
  int           x1, y1, x2, y2,
                w = _tft.width(),
                h = _tft.height();

  _tft.fillScreen(ILI9341_BLACK);
  yield();
  
  x1 = y1 = 0;
  y2    = h - 1;
  start = micros();
  for(x2=0; x2<w; x2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  x2    = w - 1;
  for(y2=0; y2<h; y2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  t     = micros() - start; // fillScreen doesn't count against timing

  yield();
  _tft.fillScreen(ILI9341_BLACK);
  yield();

  x1    = w - 1;
  y1    = 0;
  y2    = h - 1;
  start = micros();
  for(x2=0; x2<w; x2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  x2    = 0;
  for(y2=0; y2<h; y2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  t    += micros() - start;

  yield();
  _tft.fillScreen(ILI9341_BLACK);
  yield();

  x1    = 0;
  y1    = h - 1;
  y2    = 0;
  start = micros();
  for(x2=0; x2<w; x2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  x2    = w - 1;
  for(y2=0; y2<h; y2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  t    += micros() - start;

  yield();
  _tft.fillScreen(ILI9341_BLACK);
  yield();

  x1    = w - 1;
  y1    = h - 1;
  y2    = 0;
  start = micros();
  for(x2=0; x2<w; x2+=6) _tft.drawLine(x1, y1, x2, y2, color);
  x2    = 0;
  for(y2=0; y2<h; y2+=6) _tft.drawLine(x1, y1, x2, y2, color);

  yield();
  return micros() - start;
}

unsigned long Screen::testFastLines(uint16_t color1, uint16_t color2) {
  unsigned long start;
  int           x, y, w = _tft.width(), h = _tft.height();

  _tft.fillScreen(ILI9341_BLACK);
  start = micros();
  for(y=0; y<h; y+=5) _tft.drawFastHLine(0, y, w, color1);
  for(x=0; x<w; x+=5) _tft.drawFastVLine(x, 0, h, color2);

  return micros() - start;
}

unsigned long Screen::testRects(uint16_t color) {
  unsigned long start;
  int           n, i, i2,
                cx = _tft.width()  / 2,
                cy = _tft.height() / 2;

  _tft.fillScreen(ILI9341_BLACK);
  n     = min(_tft.width(), _tft.height());
  start = micros();
  for(i=2; i<n; i+=6) {
    i2 = i / 2;
    _tft.drawRect(cx-i2, cy-i2, i, i, color);
  }

  return micros() - start;
}

unsigned long Screen::testFilledRects(uint16_t color1, uint16_t color2) {
  unsigned long start, t = 0;
  int           n, i, i2,
                cx = _tft.width()  / 2 - 1,
                cy = _tft.height() / 2 - 1;

  _tft.fillScreen(ILI9341_BLACK);
  n = min(_tft.width(), _tft.height());
  for(i=n; i>0; i-=6) {
    i2    = i / 2;
    start = micros();
    _tft.fillRect(cx-i2, cy-i2, i, i, color1);
    t    += micros() - start;
    // Outlines are not included in timing results
    _tft.drawRect(cx-i2, cy-i2, i, i, color2);
    yield();
  }

  return t;
}

unsigned long Screen::testFilledCircles(uint8_t radius, uint16_t color) {
  unsigned long start;
  int x, y, w = _tft.width(), h = _tft.height(), r2 = radius * 2;

  _tft.fillScreen(ILI9341_BLACK);
  start = micros();
  for(x=radius; x<w; x+=r2) {
    for(y=radius; y<h; y+=r2) {
      _tft.fillCircle(x, y, radius, color);
    }
  }

  return micros() - start;
}

unsigned long Screen::testCircles(uint8_t radius, uint16_t color) {
  unsigned long start;
  int           x, y, r2 = radius * 2,
                w = _tft.width()  + radius,
                h = _tft.height() + radius;

  // Screen is not cleared for this one -- this is
  // intentional and does not affect the reported time.
  start = micros();
  for(x=0; x<w; x+=r2) {
    for(y=0; y<h; y+=r2) {
      _tft.drawCircle(x, y, radius, color);
    }
  }

  return micros() - start;
}

unsigned long Screen::testTriangles() {
  unsigned long start;
  int           n, i, cx = _tft.width()  / 2 - 1,
                      cy = _tft.height() / 2 - 1;

  _tft.fillScreen(ILI9341_BLACK);
  n     = min(cx, cy);
  start = micros();
  for(i=0; i<n; i+=5) {
    _tft.drawTriangle(
      cx    , cy - i, // peak
      cx - i, cy + i, // bottom left
      cx + i, cy + i, // bottom right
      _tft.color565(i, i, i));
  }

  return micros() - start;
}

unsigned long Screen::testFilledTriangles() {
  unsigned long start, t = 0;
  int           i, cx = _tft.width()  / 2 - 1,
                   cy = _tft.height() / 2 - 1;

  _tft.fillScreen(ILI9341_BLACK);
  start = micros();
  for(i=min(cx,cy); i>10; i-=5) {
    start = micros();
    _tft.fillTriangle(cx, cy - i, cx - i, cy + i, cx + i, cy + i,
      _tft.color565(0, i*10, i*10));
    t += micros() - start;
    _tft.drawTriangle(cx, cy - i, cx - i, cy + i, cx + i, cy + i,
      _tft.color565(i*10, i*10, 0));
    yield();
  }

  return t;
}

